INSERT INTO `categories` (`id`, `title`, `description`, `image`) VALUES
(1, 'Футбол', 'Футбол — командный вид спорта, в котором целью является забить мяч в ворота соперника ногами или другими частями тела (кроме рук) большее количество раз, чем команда соперника. В настоящее время самый популярный и массовый вид спорта в мире.', 'https://russian.eurasianet.org/sites/default/files/styles/article/public/2022-12/SKXn0pF4I78.jpg?itok=DQZXFwc-'),
(2, 'Шахматы', 'Шахматы — настольная игра, в которой принимают участие две стороны: одна играет белыми фигурами, другая — черными. Основная цель — поставить мат сопернику, то есть захватить вражеского короля и защитить своего. ', 'https://photobooth.cdn.sports.ru/preset/post/a/af/23276a4b5440ab49d327db805e9bb.jpeg'),
(3, 'Снукер', 'snooker — разновидность бильярдной лузной игры. Сначала была распространена в Британской империи (в Великобритании, Индии и Австралии), но с конца 1970-х годов стала известной практически во всём мире, особенно в Европе и Китае. ', 'https://osullivan.ru/upload/news_3/snooker_01.jpg'),
(4, 'Теннис', 'Теннис – вид спорта, в котором два игрока или две команды по два человека соперничают между собой. Цель каждого из игроков/команд – перекинуть ракеткой мяч на сторону соперника таким образом, чтобы соперник не смог его отразить.', 'https://gallery.profi.ru/mkt/emsender/blog/pages/190/1173_1707_369.jpeg'),
(5, 'Баскетбол', 'В баскетбол играют две команды, обычно по десять человек, от каждой из которых на площадке одновременно присутствует пять игроков. Цель каждой команды в баскетболе — забросить мяч в корзину соперника и помешать другой команде овладеть мячом.', 'https://obzor.lt/images/news/11/2022_11_12/zYJZGHJlHkuhB1idlaz9hw.jpg'),
(6, 'Хоккей', 'Хоккей — командная спортивная игра на льде,в коньках, шайба клюшка, цель шайба в воротах иной команды ворота не пропустить в свои. Игра является олимпийским видом спорта. Зарегистрировано более миллиона игроков, регулярно выступающих в различных лигах.', 'https://100-faktov.ru/wp-content/uploads/2022/11/6391879df869351df861.jpg');

